import React from 'react';
import { motion } from 'framer-motion'

const exitVar = {
    hidden: {
        width: "0vw"
    },
    visable: {
        width: '100vw'
    }
}

const Introduction = () => {
    return (
        <motion.div
            key="intro"
            className="intro-overlay"
            initial={"hidden"}
            animate={"visable"}
            variants={exitVar}
            transition={{
                duration: 1,
                ease: [0.6, 0.05, -0.01, 0.9],
            }}
        />

    )
}

export default Introduction;
