import React from 'react';
import { motion } from 'framer-motion'

const exitVar = {
    hidden: {
        width: "0vw"
    },
    visable: {
        width: '100vw'
    }
}

const stringVariant = {
    before: {
        y: 0
    },
    after: {
        y: 0,
        transition: {
            delayChildren: 0.6,
            staggerChildren: 0.04
        }
    }
}

const letterVariant = {
    before: {
        y: 400,
        transition: {
            duration: 1,
            ease: [0.6, 0.05, -0.01, 0.9],
        },
    },
    after: {
        y: 0,
        transition: {
            duration: 1,
            ease: [0.6, 0.05, -0.01, 0.9],
        },
    }
}

const Introduction = () => {

    const string = "worksbyscott.uk"

    const replaceSpace = (letter) => {
        if (letter === " ")
            return "\u00A0";
        return letter;
    }

    const heroString = Array.from(string).map((letter) => replaceSpace(letter));

    return (
        <motion.div
            key="intro"
            className="intro-overlay"
            initial={"hidden"}
            animate={"visable"}
            variants={exitVar}
            transition={{
                duration: 1,
                ease: [0.6, 0.05, -0.01, 0.9],
            }}
        >

            <motion.h3>
                {heroString.map(({ letter, index }) => (
                    <motion.span
                    >
                        {letter}
                    </motion.span>
                ))}
            </motion.h3>

        </motion.div>

    )
}

export default Introduction;
