import React from "react";
import { AnimatePresence } from "framer-motion";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

//Styles
import "./styles/App.scss";

//Componenets Pages

import Home from './pages/home';
import Cipher from './pages/projects/cipher'
import Cella from './pages/projects/cipher'
import Echo from './pages/projects/cipher'
import About from './pages/about'
import Contact from './pages/contact'

//Routing array

const routes = [
  { page: "Home", title: "Scott Templeton Portfolio", path: '/', Component: Home },
  { page: "Cipher", title: "Encoded Messenger", path: '/cipher', Component: Cipher },
  { page: "Cella", title: "Online Community Radio Station", path: '/cella', Component: Cella },
  { page: "Echo", title: "Concept Branding", path: '/echo', Component: Echo },
  { page: "About", title: "About me", path: '/about', Component: About },
  { page: "Contact", title: "Let's connect", path: '/contract', Component: Contact },
]

function App() {
  return (
    <>
      <Router>
        <Route render={({ location }) => (
          <Switch location={location} key={location.pathname}>
            {routes.map(({ path, Component }) => {
              <Route
                exact
                path={path}
                key={path}
                render={() => <Component />} />
            })}
          </Switch>
        )} />
      </Router>
    </>
  );
}

export default App;
