import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'

const variants = {
    hidden: {
        opacity: 0
    },
    visable: {
        opacity: 1
    }
}

const PageTitle = ({ headline, subhead, action, url }) => {
    return (
        <div className="row">
            <motion.div
                key="pageHeader"
                className="headline"
                intial={{
                    x: 40,
                    opacity: 0
                }}
                animate={{
                    x: 0,
                    opacity: 1
                }}
                transition={{
                    duration: 1,
                    ease: [0.6, 0.05, -0.01, 0.9],
                }}>
                <h1>{headline}</h1>
                {subhead && <h2>{subhead}</h2>}
                <div className="action">
                    <Link to={url}>
                        <a>{action}   â†’</a>
                    </Link>
                </div>
            </motion.div>
        </div>

    );
}

export default PageTitle;
