import React from 'react';
import { motion } from 'framer-motion'

const variants = {
    initial: {
        width: "100vw"
    },
    animate: {
        width: '0vw'
    }
}

const Overlay = () => {
    return (


        <motion.div
            key="intro"
            className="intro-overlay"
            initial={"initial"}
            animate={"animate"}
            variants={variants}

            transition={{
                duration: 1,
                ease: [0.6, 0.05, -0.01, 0.9],
            }}
        />

    );
}

export default Overlay;
