import React from 'react';
import { motion } from 'framer-motion'

const transition = { duration: 1.4, ease: [0.6, 0.01, -0.05, 0.9] };

const exitVar = {
    hidden: {
        width: "0vw"
    },
    visable: {
        width: '100vw'
    }
}

const lineVariant = {
    initial: {
        y: 0,
        transition: {
            delay: 2
        }
    },
    animate: {
        y: 0,
        transition: {
            delay: 3,
            delayChildren: 0.6,
            staggerChildren: 0.04,
            staggerDirection: 1,
        },
    },
}

const letterVariant = {
    initial: {
        y: 400,
    },
    animate: {
        y: 0,
        transition: { duration: 1, ...transition },
    },
};

const Introduction = () => {

    const string = "worksbyscott.uk"

    const replaceSpace = (letter) => {
        if (letter === " ")
            return "\u00A0";
        return letter;
    }

    const heroString = Array.from(string).map((letter) => replaceSpace(letter));

    return (
        <>
            <motion.div
                key="intro"
                className="intro-overlay"
                initial={"hidden"}
                animate={"visable"}
                variants={exitVar}
                transition={{
                    duration: 1,
                    ease: [0.6, 0.05, -0.01, 0.9],
                }}
            >
            </motion.div>
            <div className="intro-headline">
                <motion.h3 variants={lineVariant}>
                    {heroString.map((letter, index) => (
                        <motion.span
                            key={index}
                            variants={letterVariant}>
                            {letter}
                        </motion.span>
                    ))}
                </motion.h3>

            </div>
        </>
    )
}

/*
<motion.h3 variants={lineVariant}>
{heroString.map(({ letter, index }) => (
    <motion.span variants={letterVariant}>
        {letter}
    </motion.span>
))}
</motion.h3>
*/

export default Introduction;
