import React from 'react';
import framer, { motion } from 'framer-motion'

const exitVar = {
    hidden: {
        width: 0
    },
    visable: {
        width: '100vw'
    }
}

const Introduction = () => {
    return (
        <>
            <div className="introduction">

            </div>
            <motion.div
                key="intro"
                className="intro-overlay"
                animate={"visable"}
                exit={"hidden"}
                variants={exitVar}

                transition={{
                    duration: 1,
                    ease: [0.6, 0.05, -0.01, 0.9],
                }}
            />
        </>
    )
}

export default Introduction;
