import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'

import { ReactComponent as RightArrow } from '../images/arrow-right.svg'

const variants = {
    hidden: {
        opacity: 0
    },
    visable: {
        opacity: 1
    }
}

const PageTitle = ({ headline, subhead, action, url }) => {
    return (
        <div className="row">
            <motion.div
                key={"pageTitle"}
                className="headline"
                intial={'hidden'}
                animate={'visable'}
                variants={variants}>
                <h1>{headline}</h1>
                {subhead && <h2>{subhead}</h2>}
                <div className="action">
                    <Link to={url}>
                        <a>{action}   â†’</a>
                    </Link>
                </div>
            </motion.div>
        </div>

    );
}

export default PageTitle;
